CREATE TABLE IF NOT EXISTS butelkowanie (  idbutelkowanie SERIAL,  pojemnosc INT NOT NULL,  rodzaj_butelki VARCHAR(250) NOT NULL,  kolor_butelki VARCHAR(250) NOT NULL,  rodzaj_etykiety VARCHAR(250) NOT NULL);CREATE TABLE IF NOT EXISTS Chmiel (  idpochodzenie SERIAL,  kraj VARCHAR(200) NOT NULL,  rodzaj VARCHAR(200) NOT NULL);CREATE TABLE IF NOT EXISTS dane_producenta (  id_kraj SERIAL,  numer_telefonu INTEGER NOT NULL,  ulica VARCHAR(50) NOT NULL,  kod_pocztowy INTEGER NOT NULL,  numer_budynku INTEGER NOT NULL);CREATE TABLE IF NOT EXISTS Hurtownia (  idhurtownia SERIAL);CREATE TABLE IF NOT EXISTS przedstawiciel (  idprzedstawiciel SERIAL,  imie VARCHAR(200) NOT NULL,  nazwisko VARCHAR(200) NOT NULL,  kontakt INTEGER NOT NULL);CREATE TABLE IF NOT EXISTS importer (  idimporter SERIAL,  Nazwa VARCHAR(200) NOT NULL,  kraj VARCHAR(200) NOT NULL);CREATE TABLE IF NOT EXISTS opinie (  idopinie SERIAL,  smak VARCHAR(250) NOT NULL,  barwa VARCHAR(250) NOT NULL,  moc INTEGER NOT NULL,  stosunek_ceny_do_jakosci INTEGER NOT NULL);CREATE TABLE IF NOT EXISTS komentarz (	idkomentarz SERIAL,	tresc VARCHAR(250) NOT NULL,	idopinie INTEGER NULL FOREIGN KEY REFERENCES opinie(idopinie) ON DELETE NO ACTION);CREATE TABLE IF NOT EXISTS producent (  idproducent SERIAL,  nazwa VARCHAR(200) NOT NULL,  opis  VARCHAR(200) NOT NULL);CREATE TABLE IF NOT EXISTS adres_hurtowni (  idadres_hurtowni SERIAL,  idhurtownia INTEGER NOT NULL FOREIGN KEY REFERENCES Hurtownia(idhurtownia) ON UPDATE CASCADE,  ulica VARCHAR(255) NOT NULL,  numer INTEGER NOT NULL,  kod_pocztowy INTEGER NOT NULL,  kraj VARCHAR(255) NOT NULL,);CREATE TABLE IF NOT EXISTS importer_has_hurtownie (  id_importer_hurtowni SERIAL,  idimporter INTEGER NOT NULL,  idhurtownia INTEGER NOT NULL,  FOREIGN KEY(idimporter) REFERENCES importer(idimporter) ON update CASCADE,  FOREIGN KEY(idhurtownia) REFERENCES Hurtownia(idhurtownia) ON update CASCADE);CREATE TABLE IF NOT EXISTS kontakt (  idkontakt SERIAL,  idhurtownia INTEGER NOT NULL,  numer_tel_kom INTEGER NOT NULL,  numer_tel_stacj INTEGER NOT NULL,  fax INTEGER NOT NULL,  mail VARCHAR(250) NOT NULL,  FOREIGN KEY(idhurtownia) REFERENCES Hurtownia(idhurtownia)ON UPDATE CASCADE);CREATE TABLE IF NOT EXISTS Piwo (  idPiwo SERIAL,  idpochodzenie INTEGER NOT NULL,  idopinie INTEGER NOT NULL,  FOREIGN KEY(idpochodzenie) REFERENCES Chmiel(idpochodzenie) ON DELETE CASCADE,  FOREIGN KEY(idopinie) REFERENCES opinie(idopinie) ON DELETE CASCADE);CREATE TABLE IF NOT EXISTS Piwo_has_butelkowanie (  idbutelka_piwa SERIAL,  idpochodzenie INTEGER NOT NULL,  idPiwo INTEGER NOT NULL,  idbutelkowanie INTEGER NOT NULL,  FOREIGN KEY (idpochodzenie) REFERENCES Chmiel(idpochodzenie) ON update CASCADE,  FOREIGN KEY (idPiwo) REFERENCES Piwo(idpiwo) ON update CASCADE,  FOREIGN KEY (idbutelkowanie) REFERENCES butelkowanie(idbutelkowanie) ON update CASCADE);CREATE TABLE IF NOT EXISTS producent_has_Hurtownia (  idprod_hurt SERIAL,  idhurtownia INTEGER NOT NULL,  FOREIGN KEY(idhurtownia) REFERENCES Hurtownia(idhurtownia) ON DELETE CASCADE);CREATE TABLE IF NOT EXISTS producent_has_importer (  idprod_imp SERIAL,  idimporter INTEGER NOT NULL,  FOREIGN KEY(idimporter) REFERENCES importer(idimporter) ON DELETE CASCADE);CREATE TABLE IF NOT EXISTS producent_has_Piwo (  idproducent_piwa SERIAL,  idpochodzenie INTEGER NOT NULL,  idPiwo INTEGER NOT NULL,  FOREIGN KEY(idpochodzenie) REFERENCES Chmiel(idpochodzenie) ON update CASCADE,  FOREIGN KEY(idPiwo) REFERENCES Piwo(idPiwo) ON update CASCADE);CREATE TABLE IF NOT EXISTS hurtownie_has_przedstawiciel (  idprzedstawicielhurtowni SERIAL,  idprzedstawiciel INTEGER NOT NULL,  idhurtownia INTEGER NOT NULL,  FOREIGN KEY(idhurtownia) REFERENCES Hurtownia(idhurtownia) ON update CASCADE,  FOREIGN KEY(idprzedstawiciel) REFERENCES przedstawiciel(idprzedstawiciel) ON update CASCADE);CREATE TABLE IF NOT EXISTS rodzaj_piwa (  idrodzaj_piwa SERIAL,  idpochodzenie INTEGER NOT NULL,  idPiwo INTEGER NOT NULL,  ciemne VARCHAR(23) NULL,  jasne VARCHAR(23) NULL,  smakowe VARCHAR(30) NULL,  FOREIGN KEY(idpochodzenie) REFERENCES Chmiel(idpochodzenie) ON update CASCADE,  FOREIGN KEY(idPiwo) REFERENCES Piwo(idpiwo) ON update CASCADE);---INSERT INTO butelkowanie(idbutelkowanie,pojemnosc,rodzaj_butelki, kolor_butelki, rodzaj_etykiety)VALUES (1,300,2,'niebieska','mala'),(2,500,2,'czerwona','duza'),(3,700,2,'zielona','duza'),(4,500,2,'czarny','mala'),(5,800,3,'biala','mala');INSERT INTO Chmiel(idpochodzenie,kraj,rodzaj)VALUES (1,'niemcy','ciemny'),(2,'polska','ciemny'),(4,'rosja','ciemny'),(3,'niemcy','jasny'),(5,'chiny','jasny');INSERT INTO dane_producenta (id_kraj,numer_telefonu,ulica, kod_pocztowy,numer_budynku)VALUES (1,23232345,'DSFSDF',87,2),(2,23534323,'DSFSDF',87,2),(3,23454323,'DSFSDF',45,2),(4,23232344,'DSFSDF',32,2),(5,23232366,'DSFSDF',87,2);INSERT INTO Hurtownia(idhurtownia)VALUES (1), (2), (3), (4), (5), (6), (7);INSERT INTO przedstawiciel(idprzedstawiciel,imie, nazwisko, kontakt)VALUES   (1,'piotr','grucz',53242343),(2,'kamil','nowak',22223123),(3,'greg','baja',22311112),(4,'donald','ziemniak',9993832),(5,'chynj','abaas',332323);INSERT INTO importer(idimporter,nazwa,kraj)VALUES(1,'grujek','polska'),(2,'nefph','anglia'),(3,'zenix','holandia'),(4,'dex','irlandia'),(5,'armani','niemcy'),(6,'gifox','szwjcaria');INSERT INTO opinie(idopinie,smak,barwa,stosunek_ceny_do_jakosci,moc)VALUES (1,2,1,5,2),(2,2,3,3,3),(3,2,1,1,40),(4,1,2,2,5),(5,2,2,1,5);INSERT INTO komentarz(tresc,idopinie)VALUES('WYSMIENITE PIWO !', 1),('SlABE',2),('CALKIEM DOBRE', 1),('FATALNE', 3),('IDEALNE', 1);INSERT INTO Producent(idproducent,nazwa,opis)VALUES (1,'grzyszkowiak','pomorze'),(3,'tyskie','karpacz'),(2,'vox','statry'),(4,'warka','poznan'),(5,'browar','malopolska');INSERT INTO adres_hurtowni (idadres_hurtowni,idhurtownia,ulica,numer,kod_poc